import React from 'react'

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = (
      {
        values:
        {
          groupingVariable: {
            name: "SMN2",
            trueIf: {
              value: "1",
              operator: "eq" // "eq" | "gt" | "gte" | "lt" | "lte"
            },
            label: {
              true: "",
              false: ""
            }
          },
          covariates: [
            {
              "name": "",
              "label": "",
              "type": "", // "categorical" | "bucketized" | "continous"
              "unit": 1, // for bucketized or continuous variable only
              "cutoffs": [], // for bucketized variable only
              "values": [], // for categorical variable only
              "keys": [] // optional; for bucketized or categorical variable only
            }
          ]
        }
      }
    );


    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleChange_g_name = (event) => {
    const values = this.state.values;
    values.groupingVariable.name = event.target.value;
    console.log("asdasdsad")
    this.setState({ values });
  }

  handleChange_g_TrueIf = (event) => {
    const values = this.state.values;
    values.groupingVariable.trueIf.operator = event.target.value;
    this.setState({ values });
  }

  handleChange_g_value = (event) => {
    const values = this.state.values;
    values.groupingVariable.trueIf.value = event.target.value;
    this.setState({ values });
  }

  handleChange_g_TrueLable = (event) => {
    const values = this.state.values;
    values.groupingVariable.label.true = event.target.value;
    this.setState({ values });
  }

  handleChange_g_FalseLable = (event) => {
    const values = this.state.values;
    values.groupingVariable.label.false = event.target.value;
    this.setState({ values });
  }



  handleReset = () => {
    console.log("reset test");
    this.setState = (
      {
        values:
        {
          groupingVariable: {
            name: "SMN2",
            trueIf: {
              value: "1",
              operator: "eq" // "eq" | "gt" | "gte" | "lt" | "lte"
            },
            label: {
              true: "",
              false: ""
            }
          },
          covariates: [
            {
              "name": "",
              "label": "",
              "type": "", // "categorical" | "bucketized" | "continous"
              "unit": 1, // for bucketized or continuous variable only
              "cutoffs": [], // for bucketized variable only
              "values": [], // for categorical variable only
              "keys": [] // optional; for bucketized or categorical variable only
            }
          ]
        }
      }
    );
  }


  handleSubmit = (event) => {
    console.log(this.state.values);
    event.preventDefault();
  }

  render() {
    return (
      <form id="myForm" onSubmit={this.handleSubmit}>

        <h2>Grouping variable</h2>
        <label>
          name:
          <select value={this.state.values.groupingVariable.name} onChange={this.handleChange_g_name.bind(this)}>
            <option value="SMN">SMN</option>
            <option value="SMN2">SMN2</option>
          </select>
        </label><br />

        <label>
          True if...:
          <select value={this.state.values.groupingVariable.trueIf.operator} onChange={this.handleChange_g_TrueIf.bind(this)}>
            <option value="eq">eq</option>
            <option value="gt">gt</option>
            <option value="gte">gte</option>
            <option value="lt">lt</option>
            <option value="lte">lte</option>
          </select>
        </label>

        <label>
          value
        <input type="number" value={this.state.values.groupingVariable.trueIf.value} onChange={this.handleChange_g_value.bind(this)} />
        </label><br />

        <label>
          "True" group label:
        <input type="text" value={this.state.values.groupingVariable.label.true} onChange={this.handleChange_g_TrueLable.bind(this)} />
        </label><br />

        <label>
          "False" group label:
        <input type="text" value={this.state.values.groupingVariable.label.false} onChange={this.handleChange_g_FalseLable.bind(this)} />
        </label><br />


        <h2>Covariate</h2>

        <h4>variable 1</h4>





        <input type="button" onClick={this.handleReset.bind(this)} value="Reset" /><br />
        <input type="submit" value="Submit" /><br />
      </form>
    );
  }
}

export default Form
