import React from 'react'

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = (
      {value:
        {
        "groupingVariable": {
          "name": "",
          "trueIf": {
            "value": "",
            "operator": "" // "eq" | "gt" | "gte" | "lt" | "lte"
          },
          "label": {
            "true": "",
            "false": ""
          }
        },
        "covariates": [
          {
            "name": "",
            "label": "",
            "type": "", // "categorical" | "bucketized" | "continous"
            "unit": 1, // for bucketized or continuous variable only
            "cutoffs": [], // for bucketized variable only
            "values": [], // for categorical variable only
            "keys": [] // optional; for bucketized or categorical variable only
          }
        ]
       }
      }
    );


    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange_g_name = (event) => {
    this.setState({value: event.target.value});
  }

  handleChange_g_TrueIf = (event) => {
    this.setState({value: event.target.value});
  }

  handleChange01= (event) => {
    this.setState({value_1: event.target.value_1});
  }

  handleReset =() => {
    this.setState({value: 'coconut',value_1: 'xxx'});
  }

  handleSubmit = (event) => {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form id="myForm" onSubmit={this.handleSubmit}>
        <h2>Grouping variable</h2>
        <label>
        name:
          <select value={this.state.value} onChange={this.handleChange_g_name.bind(this)}>
            <option value="SMN">SMN</option>
            <option value="SMN2">SMN2</option>
          </select>
        </label><br />
        <label>
        True if...:
          <select value={this.state.value} onChange={this.handleChange_g_TrueIf.bind(this)}>
            <option value="eq">eq</option>
            <option value="gt">gt</option>
            <option value="gte">gte</option>
            <option value="lt">lt</option>
            <option value="lte">lte</option>
          </select>
        </label> 
        <label>
          value
        <input value={this.state.value_1} onChange={this.handleChange01} />
        </label><br />
        <label>
         "True" group label:
        <input value={this.state.value_1} onChange={this.handleChange01} />
        </label><br />
        <label>
        "False" group label:
        <input value={this.state.value_1} onChange={this.handleChange01} />
        </label><br />


        <h2>Covariate</h2>   

        <h4>variable 1</h4>





        <input type="button" onClick={this.handleReset.bind(this)} value="Reset" /><br />
        <input type="submit" value="Submit" /><br />
      </form>
    );
  }
}

export default Form
