import React from 'react'

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({value: 'coconut', value_1: 'xxx'});

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleReset = this.handleReset.bind(this);
  }

  handleChange = (event) => {
    this.setState({value: event.target.value});
  }

  handleChange01= (event) => {
    this.setState({value_1: event.target.value_1});
  }

  handleReset =() => {
    this.setState({value: 'coconut',value_1: 'xxx'});
  }

  handleSubmit = (event) => {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form id="myForm" onSubmit={this.handleSubmit}>
        <label>
          Pick your favorite flavor:
          <select value={this.state.value} onChange={this.handleChange}>
            <option value="grapefruit">Grapefruit</option>
            <option value="lime">Lime</option>
            <option value="coconut">Coconut</option>
            <option value="mango">Mango</option>
          </select>
        </label><br />
        <input value={this.state.value_1} onChange={this.handleChange01} /><br />
        <input type="button" onClick={this.handleReset} value="Reset" /><br />
        <input type="submit" value="Submit" /><br />
      </form>
    );
  }
}

export default Form
